import React, { useState, useRef, useEffect } from "react";
import recipestyle from "../assets/addrecipe.module.css";
import { Link, useNavigate, useLocation } from "react-router-dom";
import Footers from "../Component/footer";
import axios from "axios";

const UpdateRecipe = () => {
  let location = useLocation();
  const navigate = useNavigate();
  const hiddenFileInput = useRef(null);
  // const navigate = useNavigate();
  const [image, setImage] = useState("");
  const [idRecipe, setIdRecipe] = useState("");
  // const [title, setTitle] = useState("");
  //   const [ingredient, setIngredient] = useState("");
  //   const [video, setVideo] = useState("");

 //update recipe
    const handleUpdate = (form) => {
        axios
        .put(`${process.env.REACT_APP_BACKEND_URL}/recipe/update/${idRecipe}`, form, {
            headers: {
                "Content-Type": "multipart/form-data",
            }
        })
        .then((res) => {
            console.log(res);
            setImage("");
            alert("Recipe updated successfully");
        //   formPost.reset();
        })
        .catch((err) => {
            console.log(err);
            alert("Failed to update recipe");
        });
    };
    useEffect(() => {
      if (location.state) {
        console.log(location.state);
        // setIdRecipe(location.state.id);
        // setTitle(location.state.title);
        // setIngredient(location.state.ingredients);
        // setVideo(location.state.video);
      } else {
        return navigate("/profile", { replace: true });
      }
    }, []);

    const handleSubmit = event => {
        event.preventDefault();
        let formData = new FormData(event.target);
        formData.append("image", image);
        console.log(Object.fromEntries(formData));
        handleUpdate(Object.fromEntries(formData));
    };

  const handleClick = (event) => {
    hiddenFileInput.current.click();
  };
  const handleChange = (event) => {
    const fileUploaded = event.target.files[0];
    document.getElementById("customBtn").innerHTML = fileUploaded.name;
    setImage(fileUploaded);
  };

  return (
    <div className={recipestyle.customBody}>
      <header className={recipestyle.customHeader}>
        <nav className="navbar navbar-expand-lg bg-transparent fixed-top p-4">
          <div className="container">
            <button
              className="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span className="navbar-toggler-icon"></span>
            </button>
            <div className="collapse navbar-collapse" id="navbarNav">
              <ul className="navbar-nav">
                <li className="nav-item px-3">
                  <Link
                    className={`nav-link active ${recipestyle.customNav}`}
                    aria-current="page"
                    to="/landing"
                  >
                    Home
                  </Link>
                </li>
                <li className="nav-item px-3">
                  <Link
                    className={`nav-link active ${recipestyle.customNav}`}
                    to="/add"
                  >
                    Add Recipe
                  </Link>
                </li>
                <li className="nav-item px-3">
                  <Link
                    className={`nav-link active ${recipestyle.customNav}`}
                    to="/profile"
                  >
                    Profile
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </header>
      <main>
        <section>
          <div className="container mt-5">
            <form className="mx-5">
              <div className="mb-3">
                <div className={recipestyle.rectangle}>
                  <div>
                    <img
                      src={require("../assets/image/image.png")}
                      alt=""
                      className={recipestyle.imageAdd}
                      id="customBtn"
                    />
                  </div>

                  <h5
                    className="text-muted"
                    id="customBtn"
                    onClick={handleClick}
                  >
                    Add image
                  </h5>
                </div>
                <input
                  className={recipestyle.input}
                  type="file"
                  ref={hiddenFileInput}
                  id="formFile"
                  onChange={handleChange}
                  style={{ display: "none" }}
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className={`form-control ${recipestyle.input}`}
                  id="exampleInputEmail1"
                  placeholder="Title"
                  aria-describedby="emailHelp"
                  name="title"
                />
              </div>
              <div className="mb-3">
                <textarea
                  className={`form-control ${recipestyle.textArea}`}
                  id="exampleFormControlTextarea1"
                  rows="3"
                  placeholder="Ingredients"
                  name="ingredient"
                ></textarea>
              </div>
              <div className="mb-3">
                <textarea
                  className={`form-control ${recipestyle.textArea}`}
                  id="exampleFormControlTextarea1"
                  rows="1"
                  placeholder="Video"
                  name="videostep"
                ></textarea>
              </div>
              <div className="text-center">
                <button
                  type="submit"
                  onClick={handleSubmit}
                  className={`btn ${recipestyle.btnCustomArea}`}
                >
                  Update
                </button>
              </div>
            </form>
          </div>
        </section>
      </main>
      <Footers />
    </div>
  );
};
export default UpdateRecipe;
