  return (
    <div>
      <div className="customSplash bg-light">
        <div className="bg-white rounded-4">
          <form action="" className="d-flex justify-content-center align-items-center">
            <h4>Login</h4>
            <p>Hi, Welcome back!</p>
            <div className="form-group">
              <label htmlFor="email" className="">
                Email
              </label>
              <input
                type="text"
                className="form-control border-0"
                id="email"
                name="email"
                placeholder="Masukkan email"
                // value={form.username}
                // onChange={(e) => setForm({ ...form, username: e.target.value })}
              />
              <hr />
            </div>
            <div className="form-group">
              <label htmlFor="password" className="">
                Password
              </label>
              <input
                type="password"
                className="form-control border-0"
                id="password"
                name="password"
                placeholder="Masukkan password"
                // value={form.password}
                // onChange={(e) => setForm({ ...form, password: e.target.value })}
              />
              <hr />
            </div>
            <div className="text-end">
              <Link className="aDiv" to="/forgot">
                Forgot Password ?
              </Link>
            </div>
            <div className="text-center">
              <button
                type="submit"
                className="btn btn-primary btn-block rounded-pill"
                // onClick={onSubmit}
              >
                Login
              </button>
            </div>
            <div className="row">
              <div className="col-md-4">
                <hr />
              </div>
              <div className="col-md-4 text-center">
                Login with
              </div>
              <div className="col-md-4">
                <hr />
              </div>
            </div>
            <div className="text-center">
              <Link className="btn btn-primary btn-block rounded-pill" to="/register">
                <i className="fa fa-google"> Google</i>
              </Link>
            </div>
            <div className="text-center">
                <p className="text-muted">
                  Don't have a account?{" "}
                  <Link className='ap'to="/">
                    Sign Up
                  </Link>
                </p>
              </div>
          </form>
        </div>
      </div>
    </div>
  );
};
export default Index;
