import React, {useState, useEffect} from "react";
import { Link, useNavigate } from "react-router-dom";
import detailstyle from "../assets/detailrecipe.module.css";
import Footers from "../Component/footer";
import axios from "axios";

const DetailRecipe = () => {
 //update recipe
    // const handleUpdate = (form) => {
    //     axios
    //     .put(`${process.env.REACT_APP_BACKEND_URL}/recipe/update/${idRecipe}`, form, {
    //         headers: {
    //             "Content-Type": "multipart/form-data",
    //         }
    //     })
    //     .then((res) => {
    //         console.log(res);
    //         setImage("");
    //         return navigate("/detail");
    //         alert("Recipe updated successfully");
    //     //   formPost.reset();
    //     })
    //     .catch((err) => {
    //         console.log(err);
    //         alert("Failed to update recipe");
    //     });
    // };
    //handle ketika id recipe tidak ada
    // useEffect(() => {
    //   if (state) {
    //     setIdRecipe(state.id);
    //     setForm({
    //       title: state.title,
    //       ingredient: state.ingredient,
    //       videostep: state.videostep,
    //     });
    //     // setImage(Buffer.from(state.image, 'binary').toString());
    //     // console.log(Buffer.from(state.image, 'binary').toString());
    //   } else {
    //     return navigate("/profile", { replace: true });
    //   }
    // }, []);
  const navigate = useNavigate();
  const [recipe, setRecipe] = useState([]);
  const [loading, setLoading] = useState(true);
  const [iserror, setIserror] = useState(false);
  let { state } = useLocation();
  // const navigate = useNavigate();
  const [image, setImage] = useState("");
  const [idRecipe, setIdRecipe] = useState("");
  const [form, setForm] = useState({
    title: "",
    ingredient: "",
    videostep: "",
  });

  useEffect(() => {
    axios
      .get(`${process.env.REACT_APP_BACKEND_URL}/recipe/list${idRecipe}`, form,{
        headers: {
          "Content-Type": "multipart/form-data",
        }
      })
      .then((res) => {
        setTimeout(() => {
          setRecipe(res.data);
          setLoading(false);
        }, 100);
        console.log(res);
      })
      .catch((err) => {
        console.log(err);
      });
  }, []);
      //handle ketika id recipe tidak ada
    useEffect(() => {
      if (state) {
        setIdRecipe(state.id);
        setForm({
          image: state.image,
          title: state.title,
          ingredient: state.ingredient,
          videostep: state.videostep,
        });
        // setImage(Buffer.from(state.image, 'binary').toString());
        // console.log(Buffer.from(state.image, 'binary').toString());
      } else {
        return navigate("/profile", { replace: true });
      }
    }, []);
  return (
    <div className={detailstyle.customBody}>
      <header class={detailstyle.customHeader}>
        <nav class="navbar navbar-expand-lg bg-transparent fixed-top p-4">
          <div class="container">
            <button
              class="navbar-toggler"
              type="button"
              data-bs-toggle="collapse"
              data-bs-target="#navbarNav"
              aria-controls="navbarNav"
              aria-expanded="false"
              aria-label="Toggle navigation"
            >
              <span class="navbar-toggler-icon"></span>
            </button>
            <div class="collapse navbar-collapse" id="navbarNav">
              <ul class="navbar-nav">
                <li class="nav-item px-3">
                  <Link
                    class={`nav-link active ${detailstyle.customNav}`}
                    aria-current="page"
                    to="/landing"
                  >
                    Home
                  </Link>
                </li>
                <li class="nav-item px-3">
                  <Link
                    class={`nav-link active ${detailstyle.customNav}`}
                    to="/add"
                  >
                    Add Recipe
                  </Link>
                </li>
                <li class="nav-item px-3">
                  <Link
                    class={`nav-link active ${detailstyle.customNav}`}
                    to="/profile"
                  >
                    Profile
                  </Link>
                </li>
              </ul>
            </div>
          </div>
        </nav>
      </header>
      <main>
        <section class="container mt-5">
          <div class={detailstyle.customDetail}>
            <h1 class={`${detailstyle.h1} mb-4`}
            >{form.title}</h1>
            <div class="position-relative">
              <img
                src={require("../assets/image/makanan1.png")}
                alt="chicken"
                class={`img-fluid ${detailstyle.customImageFood}`}
              />
              <button type="button" class={`btn ${detailstyle.btnSave}`}>
                <img src={require("../assets/image/Group 73.png")} alt="" />
              </button>
              <button type="button" class={`btn ${detailstyle.btnLike}`}>
                <img src={require("../assets/image/Vector (1).png")}  alt="" />
              </button>
            </div>
          </div>
          <div class={`${detailstyle.customIngredient} my-5`}>
            <h2>Ingredients</h2>
            <ul>
              <li>2 eggs</li>
              <li>2 tbsp mayonnaise</li>
              <li>3 slices bread</li>
              <li>a little butter</li>
              <li>â…“ carton of cress</li>
              <li>
                2-3 slices of tomato or a lettuce leaf and a slice of ham or
