import React, { useState, useRef } from 'react';
import recipestyle from "../assets/addrecipe.module.css";
import { Link } from 'react-router-dom';
import { useNavigate } from "react-router-dom";
import Footers from "../Component/footer";
import Navbar from "../Component/navbar";
import axios from "axios";


const AddRecipe = () => {
  const data = JSON.parse(localStorage.getItem("data"));
  const navigate = useNavigate();
    const hiddenFileInput = useRef(null);
    // const navigate = useNavigate();
    const [image, setImage] = useState("");

    // const [form, setForm] = useState({
    //     title: "",
    //     ingredients: "",
    //     video: "",
    // })

    // const onChangeInput = (e, field) => {
    //     setForm({
    //         ...form,
    //         [field]: e.target.value,
    //     });
    // }
    const handleClick = event => {
        hiddenFileInput.current.click();
    };
    const handleChange = event => {
        const fileUploaded = event.target.files[0];
        document.getElementById("customBtn").innerHTML = fileUploaded.name;
        setImage(fileUploaded);
    };
    const handleSubmit = event => {
        event.preventDefault();
        let formData = new FormData(event.target);
        formData.append("image", image);
        // console.log(Object.fromEntries(formData));
        handlePost(Object.fromEntries(formData));
    };
    // let formPost = useRef();
    const handlePost = (form) => {
        axios
        .post("http://localhost:4000/recipe/add", form, {
            headers: {
                "Content-Type": "multipart/form-data",
            }
        })
        .then((res) => {
          console.log(res);
          setImage("");
          return navigate("/detail");
          alert("Recipe added successfully");
        //   form.reset();
        })
        .catch((err) => {
          console.log(err);
          alert("Failed to add recipe");
        });
    };

  return (
    <div className={recipestyle.customBody}>
        <Navbar />
      <main>
        <section>
          <div className="container mt-5">
            <form onSubmit={handleSubmit} className="mx-5">
              <div className="mb-3">
                <div className={recipestyle.rectangle}>
                  <div>
                    <img
                      src={require("../assets/image/image.png")}
                      alt=""
                      className={recipestyle.imageAdd}
                      id="customBtn"
                    />
                  </div>

                  <h5 className="text-muted" id="customBtn" onClick={handleClick}>
                    Add image
                  </h5>
                </div>
                <input
                  className={recipestyle.input}
                  type="file"
                  ref={hiddenFileInput}
                  id="formFile"
                  onChange={handleChange}
                  style={{display: "none"}}
                />
              </div>
              <div className="mb-3">
                <input
                  type="text"
                  className={`form-control ${recipestyle.input}`}
                  id="exampleInputEmail1"
                  placeholder="Title"
                  aria-describedby="emailHelp"
                  name='title'
                />
              </div>
              <div className="mb-3">
                <textarea
                  className={`form-control ${recipestyle.textArea}`}
                  id="exampleFormControlTextarea1"
                  rows="3"
                  placeholder="Ingredients"
                  name='ingredient'
                ></textarea>
              </div>
              <div className="mb-3">
                <textarea
                  className={`form-control ${recipestyle.textArea}`}
                  id="exampleFormControlTextarea1"
                  rows="1"
                  placeholder="Video"
                  name='videostep'
                ></textarea>
              </div>
              <div className="text-center">
                <button type="submit" className={`btn ${recipestyle.btnCustomArea}`} 
                state={{ id: data.id_recipe, image: data.image, title: data.title, ingredient: data.ingredient, videostep: data.videostep}}
                >
                  Post
                </button>
              </div>
            </form>
          </div>
        </section>
      </main>
      <Footers />
    </div>
  );
};
export default AddRecipe;
